sqlMap.select:
SELECT A.LVY_KEY LVY_KEY /*부과대장키(31) */ , A.FST_AMT FST_AMT /*최초본세*/ , A.THLVY_KEY THLVY_KEY /*모번호키(31)(부가가치세인경우만)*/ , NVL(A.PER_PID, ' ') REG_NO FROM RISTNLVBOOK A /*부과대장 TBL*/ , CMMTNDPTMNT B /*부서 TBL */ , CMMTNSEMOK C /*세목코드 TBL*/ , CMMTNTAXINFO D /*세액정보 TBL*/ , CMMTNBUDGET E /*예산과목 TBL*/ , CMMTNOFFICE F /*자치단체 TBL*/ WHERE A.LVY_DEL_GBN = '02' /*부과취소구분 01.취소 02.미취소 */ AND A.THAP_GBN IN ('03') /*통합구분 01.통합 02.미통합 03.VAT */ AND A.DIV_GBN = '02' /*분납구분 01.분납 02.미분납 */ AND A.DEP_CODE = B.DEP_CODE(+) AND A.ACC_YEAR = C.ACC_YEAR(+) AND A.ACC_GBN = C.ACC_GBN(+) AND A.SEMOK_CODE = C.SEMOK_CODE(+) AND SUBSTR(A.SEMOK_CODE,1,3) IN ('299') /*과목[299]에만 적용*/ AND A.LST_AMT + A.LST_ADD_AMT + A.DIV_IJA > 0 /*(최종본세 + 최종가산금 + 분납이자) > 0 */ AND A.DEP_CODE = B.DEP_CODE(+) AND A.DEP_CODE = D.DEP_CODE(+) AND A.ACC_YEAR = D.ACC_YEAR(+) AND A.ACC_GBN = D.ACC_GBN(+) AND A.SEMOK_CODE = D.SEMOK_CODE(+) AND D.BUD_CODE = E.BUD_CODE(+) AND D.ACC_YEAR = E.ACC_YEAR(+) AND B.SF_TEAM_CODE = F.SF_TEAM_CODE AND F.SF_TEAM_NM LIKE '부산%' AND A.ACC_GBN||A.SEMOK_CODE BETWEEN '00000000' AND '99999999' AND A.ACC_YEAR BETWEEN '1900' AND '9999' AND A.PAY_GBN = '02' /*수납구분 01.수납 02.미수납 03.일부수납 */ AND A.LSS_GBN = '02' /*결손구분 01.결손 02.미결손 */ AND A.LVY_STT_GBN = '01' /*부과상태구분 01.부과 02.체납 */ AND A.THLVY_KEY IS NOT NULL /*THLVY_KEY NOT NULL 인경우만 (부가가치세인경우)*/
/*주민번호 및 과세번호를 조회...*/ AND A.PER_PID = #REG_NO#
A.LVY_NO LIKE #TAX_SNO#||'%'

sqlMap.delete:
DELETE FROM TX2112_TB /*표준세외수입 부과상세*/ WHERE TAX_NO = #LVY_KEY# /*부과대장 키(조합키) : 납세번호*/ AND TAX_CNT = 0 /*부과순번 */ AND THAP_GBN = '02' /*통합고지구분(1:통합고지,2:개별고지,3:통합고지이관 */ AND BUGWA_STAT = #BUGWA_STAT# /*부과상태 01:부과, 02:체납 */ AND SNTG IN ('0','9') /*납부여부 0:미수납, 1:수납처리중, 2:수납*/ AND EXISTS(
SELECT 'Y' FROM TX2111_TB WHERE TAX_NO = #LVY_KEY# /*부과대장 키(조합키)*/ AND TAX_ITEM = '299099' /*과세목코드*/
)

sqlMap.select:
SELECT A.LVY_KEY LVY_KEY /*부과대장키(31) */ , A.DL_FST_AMT FST_AMT /*최초본세*/ , A.DL_THLVY_KEY THLVY_KEY /*모번호키(31)(부가가치세인경우만)*/ , NVL(A.PER_PID, ' ') REG_NO FROM RISTNDLBOOK A /*체납대장 TBL*/ , CMMTNDPTMNT B /*부서 TBL */ , CMMTNSEMOK C /*세목코드 TBL*/ , CMMTNTAXINFO D /*세액정보 TBL*/ , CMMTNBUDGET E /*예산과목 TBL*/ , CMMTNOFFICE F /*자치단체 TBL*/ WHERE A.DL_THAP_GBN IN ('03') /*통합구분 01.통합 02.미통합 03.VAT */ AND A.DL_DIV_GBN = '02' /*분납구분 01.분납 02.미분납 */ AND A.DEP_CODE = B.DEP_CODE(+) AND A.ACC_YEAR = C.ACC_YEAR(+) AND A.ACC_GBN = C.ACC_GBN(+) AND A.SEMOK_CODE = C.SEMOK_CODE(+) AND SUBSTR(A.SEMOK_CODE,1,3) IN ('299') /*과목[299]에만 적용*/ AND A.DL_LST_AMT + A.DL_LST_ADD_AMT + A.DL_DIV_IJA > 0 /*(최종본세 + 최종가산금 + 분납이자) > 0 */ AND A.DEP_CODE = B.DEP_CODE(+) AND A.DEP_CODE = D.DEP_CODE(+) AND A.ACC_YEAR = D.ACC_YEAR(+) AND A.ACC_GBN = D.ACC_GBN(+) AND A.SEMOK_CODE = D.SEMOK_CODE(+) AND D.BUD_CODE = E.BUD_CODE(+) AND D.ACC_YEAR = E.ACC_YEAR(+) AND B.SF_TEAM_CODE = F.SF_TEAM_CODE AND F.SF_TEAM_NM LIKE '부산%' AND A.ACC_GBN||A.SEMOK_CODE BETWEEN '00000000' AND '99999999' AND A.ACC_YEAR BETWEEN '1900' AND '9999' AND A.DL_PAY_GBN = '02' /*수납구분 01.수납 02.미수납 03.일부수납 */ AND A.DL_LSS_GBN = '02' /*결손구분 01.결손 02.미결손 */ AND A.DL_THLVY_KEY IS NOT NULL /*THLVY_KEY NOT NULL 인경우만 (부가가치세인경우)*/
/*주민번호 및 과세번호를 조회...*/ AND A.PER_PID = #REG_NO#
A.LVY_NO LIKE #TAX_SNO#||'%'
